import React, { Component } from 'react';
import CharList from './charList';
import WordList from './wordList';

class GameWindowextends extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            word: ''
        };
    }

    modifyMessage= (data) => {
            this.setState({word: data})
        
    }
    render() { 
        return ( 
        <div>
        <button> start </button>
        <CharList/>
        <button> reroll </button>
        <div>
        word of the turn: 
        <WordList/>
        </div>
        </div>
         );
    }
}

 
export default GameWindow;
