import './App.css';
import React, { Component } from 'react';
import GameWindow from './components/gameWindow';
import text from './data/data';

class App extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    words: '',
    characters: '',
  }
  componentDidMount(){
    this.initData();
    console.log("initialized");
    console.log(this.state.data);
  }
  initData(){
    //fetch from url
      fetch(`http://localhost:3001/data`)
       .then((response) => response.json()) //make response into json
       .then((jsonObjs) => {
         this.setState({data: jsonObjs});
         this.setState({words: jsonObjs.words});
         this.setState({characters: jsonObjs.characters});
       }); //set state as jsonObjects
      console.log("out of .then");
      //const display = this.state.data.map((item) => <p>{item.body}</p>)
  }
  generateCharList(){
    var returned = [];

    for(var i = 0; i < 10; i ++){
      var added = this.statecharacters.charAt(Math.floor(Math.random() * this.state.characters.length));
      while(returned.includes(added))
        added = this.statecharacters.charAt(Math.floor(Math.random() * this.state.characters.length))
      returned.push(added);
    }

    return returned;
  }
  render() {
    return (
      <div>
        <GameWindow 
        charList = {this.generateCharList()}/>
      </div>
);
  }
}


