import React, { Component } from 'react';

/**
 * Contains The Boxes with the characters that
 * make up the word of the turn
 */
class WordBox extends React.Component {
    constructor(props){
        super(props);
    }
    modifyMessage= (arr) => {
        //this.setState({char: arr[0]});//sets char, for debugging
        //
        //sends array to GameWindow, signals to add char to word
        this.props.parentCallback(arr);
    }
    render() { 
        listItems = this.props.charBox;
        for(var i = 0; i < this.props.charBox.length; i++){
            listItems = this.modifyMessage;
        }
        return (
        <div className = "flexRow">

            {listItems}
        </div>
        );
    }
}

export default WordBox;
